import {useState} from"react"
import './App.css';
import Box from "./component/Box";


//1.박스2개 (타이틀,사진정보,결과)
//2. 가위바위보 버튼이 있다.

//3.버튼클릭시 클릭한 값이 박스에 보임
//4.컴퓨터는 랜덤하게 아이템 선택이 된다.

//5. 3 4 의 결과를 가지고 누가 이겼는지 승패를 따진다.
//6. 승패결과에 따라 테두리 색이 바뀐다.(이기면-초록,지면-빨강,비기면-검은색)


const choice = {
  rock:{
    name:"Rock",
    img:"data:image/jpeg;base64,"
  },
  scissors:{
    name:"Scissors",
    img:"https://upload.wikimedia.org/wikipedia/commons/9/97/Standard_household_scissors.jpg"
  },
  paper:{
    name:"Paper",
    img:"data:image/jpeg;base64,"
  },
}
function App() {
  const[computerSelect,computersetSelect]=useState(null)
  const[userSelect,setUserSelect] = useState(null)
  const[result,setResult]=useState("")


  const play=(userChoice) =>{
    setUserSelect(choice[userChoice])
    let computerChoice= randomChoice();
    computersetSelect(computerChoice);
    setResult(judgement(choice[userChoice],computerChoice))
  };

  const judgement = (user,computer) =>{
    console.log("user",user,"computer",computer);

    // user == computer tie
    //user == rock computer "scissors" user win
    //user == rock computer "paper" user lose
    //user == scissors com rock user lose
    //user == scissors com paper user win
    //user == paper com rock user win
    //user == paper com scissors user lose

    if(user.name == computer.name){
      return "tie"
    }else if(user.name=="Rock")
    return computer.name=="Scissors"?"win":"lose";
    else if(user.name=="Scissors")
    return computer.name=="Paper"?"win":"lose";
    else if(user.name=="Paper")
    return computer.name=="Rock"?"win":"lose";

  };

  


  const randomChoice=()=>{
    let ItemArray = Object.keys(choice); //객체에서 키값만 뽑아서 어레이로 만들어주는 함수
    console.log("item array",ItemArray)

    let randomItem = Math.floor(Math.random()*ItemArray.length)
    console.log("randomItem값은",randomItem)
    let final = ItemArray[randomItem]
    console.log("final",final)
    return choice[final];
  }

  return (
    <div>
    <div className="main">
      <Box title="You" item={userSelect} result={result}/>
      <Box title="computer" item={computerSelect} result={result}/>
      
    </div>
      <div className="main">
      <button onClick={()=> play("scissors")}>가위</button>
      <button onClick={()=> play("rock")}>바위</button>
      <button onClick={()=> play("paper")}>보</button>
      </div>
    </div>
  );
}

export default App;
